var _ = require('lodash')

const parser = payload => {
  const All = payload.trim().split('\n').map(x => x.trim().split('+').map(l => l.trim()))
  const Header = _.head(All.slice(0,1))
	const Records = All.slice(1)
  return Records.map(record => _.zipObject(Header, record))
}

module.exports = parser
